public class SumUsingFor {

	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int Number=7, i,sum =0;
    for (i=1; i<=Number; ++i)
    {
    	sum=sum+i;
    }
    System.out.println("Sum is" + sum);
	}

}
